unsigned char *hex_to_bytes(const char *hex, size_t *out_len)
{
    size_t len = strlen(hex);
    if (len % 2 != 0)
    {
        return NULL;
    }

    *out_len = len / 2;
    unsigned char *bytes = malloc(*out_len);
    if (!bytes)
        return NULL;

    for (size_t i = 0; i < *out_len; i++)
    {
        sscanf(hex + 2 * i, "%2hhx", &bytes[i]);
    }

    return bytes;
}

