    BIO_free(bio);
    if (!privkey)
    {
        handle_errors();
    }

    EVP_PKEY *pubkey = EVP_PKEY_new();
    if (!pubkey)
    {
        handle_errors();
    }

    if (EVP_PKEY_set1_RSA(pubkey, EVP_PKEY_get1_RSA(privkey)) <= 0)
    {
        handle_errors();
    }

    EVP_PKEY_free(privkey);

    if (!pubkey)
    {
        handle_errors();
    }

    char input[1024];
    printf("Enter string to verify: ");
    getchar(); // clear newline
    fgets(input, sizeof(input), stdin);
    input[strcspn(input, "\n")] = '\0';

    char hexsig[2048];
    printf("Enter signature (hex): ");
    fgets(hexsig, sizeof(hexsig), stdin);
    hexsig[strcspn(hexsig, "\n")] = '\0';

    size_t siglen;
    unsigned char *sig = hex_to_bytes(hexsig, &siglen);
    if (!sig)
    {
        return 1;
    }

    EVP_MD_CTX *mdctx = EVP_MD_CTX_new();
    if (!mdctx)
        handle_errors();

    if (EVP_DigestVerifyInit(mdctx, NULL, EVP_sha256(), NULL, pubkey) <= 0)
        handle_errors();

    if (EVP_DigestVerifyUpdate(mdctx, input, strlen(input)) <= 0)
        handle_errors();

    int verify_ok = EVP_DigestVerifyFinal(mdctx, sig, siglen);
    if (verify_ok == 1)
    {
        printf("Signature is valid.\n");
    }
    else if (verify_ok == 0)
    {
        printf("Signature is invalid.\n");
    }
    else
    {
        handle_errors();
    }

    free(sig);
    EVP_MD_CTX_free(mdctx);
    EVP_PKEY_free(pubkey);
    EVP_cleanup();
    ERR_free_strings();

    return 0;
